 using UnityEngine;  using System;  using System.Collections;  using System.Collections.Generic;  public class mandatos : MonoBehaviour {     [HideInInspector] public Rigidbody2D playerRB;     string command = "";       void Start()     {         playerRB = GameObject.FindWithTag("Player").GetComponent<Rigidbody2D>();     }      void Update()     {         detectPressedKeyOrButton();     }      public void detectPressedKeyOrButton()     {         if(Input.GetKeyDown(KeyCode.Return)) {             executeCommand(command);             command = "";             return;         }         foreach(char c in "abcdefghijklmnopqrstuvwxyz123456789")         {             string s = c.ToString();             if (Input.GetKeyDown(s))                 command += s;         }     }      public void executeCommand(String command) {         if(command == "on") {             GameObject.FindWithTag("panel").GetComponent<Background>().ToggleOn();         }     } } 